import React from "react"
// import { BrowserRouter as Router, Route, Link } from "react-router-dom"
// const SvenskaSpelApi ="https://api.www.svenskaspel.se/player/sponsorship/autocomplete?search=stockholm&numResponses=20"

class ExerciseForm extends React.Component {

  state= {
    show: true
  }
  //
  // handleChecked = () => {
  //   const checked = !this.state.checked
  // }

  addTwoPoints = (event) => {
    if (event.target.checked) {
      this.props.onScoreChange(2)
    } else {}
  }

  removeTwoPoints = () => {
    this.props.onScoreChange(-2)
  }

  addFivePoints = () => {
    this.props.onScoreChange(5)
  }

  removeFivePoints = () => {
    this.props.onScoreChange(-5)
  }

  addThreePoints = () => {
    this.props.onScoreChange(3)
  }

  removeThreePoints = () => {
    this.props.onScoreChange(-3)
  }

  ToggleClick = () => {
    this.setState({ show: !this.state.show })
  }

  render() {
    return (
      <div>
        <form>
          <label>
            10 Situps (2 p)
            <input type="checkbox" value="2p" onChange={this.addTwoPoints} />
            <input type="checkbox" value="2p" onChange={this.removeTwoPoints} />
            {this.state.show ? "hide number" : "show number"}
          </label>
          <label>
            15 Burpees (3 p)
            <input type="checkbox" value="3p" onChange={this.addThreePoints} />
          </label>
          <label>
            20 min jogging (5 p)
            <input type="checkbox" value="5p" onChange={this.addFivePoints} />
          </label>
        </form>
      </div>
    )
  }

}

export default ExerciseForm
